/*
Plugin Name: vQuiz.js
Plugin version: 1.0.0
Plugin Author: vishwajeet kumar
About Plugin: This is a small javascript plugin to create a quiz system on web with instant answer check and explanation on clicking any answer.
 */
function addExplanation(){var getHint=document.getElementById('vquiz');var AllHint=getHint.getElementsByClassName("vHint");for(var i=0;i<AllHint.length;i++){var btag=document.createElement("b");var explText=document.createTextNode("Explanation: ");btag.appendChild(explText);AllHint[i].insertBefore(btag,AllHint[i].firstChild)}} function addIcon(){var getQuiz=document.getElementById('vquiz');var getAns=getQuiz.getElementsByClassName("vAnswer");for(var i=0;i<getAns.length;i++){var checkStatus=getAns[i].dataset.vquiz;if(checkStatus=="right"){getAns[i].insertAdjacentHTML('afterbegin','<span class="vrighticon">&check; </span>')} else if(checkStatus=="wrong"){getAns[i].insertAdjacentHTML('afterbegin','<span class="vwrongicon">&cross; </span>')}}} function vquiz(){addExplanation();addIcon();var getQuiz=document.getElementById('vquiz');var quizBtn=getQuiz.getElementsByClassName("vAnswer");for(var i=0;i<quizBtn.length;i++){quizBtn[i].addEventListener("click",function(){event.preventDefault();var Qparent=this.parentNode;this.parentNode.getElementsByClassName('vHint')[0].style.display="none";var qans=Qparent.getElementsByClassName("vAnswer");for(var i=0;i<qans.length;i++){if(qans[i].className=="vAnswer vWrong"||qans[i].className=="vAnswer vRight"){qans[i].className="vAnswer";qans[i].getElementsByTagName("span")[0].style.display="none"}} if(this.dataset.vquiz=="wrong"){this.className+=" vWrong";this.getElementsByTagName("span")[0].style.display="inline"} this.parentNode.getElementsByClassName('vHint')[0].style.display="block";var qans=Qparent.getElementsByClassName("vAnswer");for(var i=0;i<qans.length;i++){var check=qans[i].dataset.vquiz;if(check=="right"){qans[i].className+=" vRight";qans[i].getElementsByTagName("span")[0].style.display="inline"}}},!0)}}